  [7, 6, 3, 4, 1, 8, 2, 5, 9],
];
 */
~~~

<br>
테스트케이스는 유효한 입력만 들어오는 스도쿠이나, 유효한지 (스도쿠 해결이 가능한 지) 판별하여 가능하면 해결하도록 작성.

## Solution

~~~js
// javascript
function solve(board) {
    if (solved(board)) {   
        return board  // 다 해결됐다면 현재 보드를 리턴
    } else {
        const possibilities = nextBoards(board)
        const validBoards = keepOnlyValid(possibilities)
        return searchForSolution(validBoards)
    }
}
// 목록에서 잘못된 보드를 모두 필터링
function keepOnlyValid(boards){
    let res = []
    for (let i = 0; i < boards.length; i++){
        if (validBoard(boards[i])){
            res.push(boards[i])
        }
    }
    return res
}
// 주어진 보드가 유효한지 확인
function validBoard(board){
    return rowsCheck(board) && columnsCheck(board) && boxesCheck(board)
}
// 다 해결된(모든 칸이 채워진) 스도쿠인지 검사
function solved(board){
    for (let i = 0; i < 9; i++){
        for (let j = 0; j < 9; j++){
            if (board[i][j] == 0){
                return false
            }
        }
    }
    return true
}
// 해결이 안되었다면 백트래킹
function searchForSolution(boards) {
    if (boards.length < 1) {
        return false
    } else { 
        let first = boards.shift() 
        const tryPath = solve(first)
        if (tryPath != false) {
           return tryPath
        } else {
           return searchForSolution(boards)
        }
    }
}
// 첫 번째 빈 자리를 찾고 해당 자리를 숫자 1...9로 채우는 9개의 다른 보드를 생성
function nextBoards(board){ 
    let res = []
    const firstEmpty = findEmptySquare(board)
    if (firstEmpty != undefined){
        const y = firstEmpty[0]
        const x = firstEmpty[1]
        for (let i = 1; i <= 9; i++){
            let newBoard = [...board]
            let row = [...newBoard[y]]
            row[x] = i
            newBoard[y] = row
            res.push(newBoard)
        }
    }
    return res
}
// 첫 번째 빈 자리에 대한 i j 좌표 가져오기)
function findEmptySquare(board){
    for (let i = 0; i < 9; i++){
        for (let j = 0; j < 9; j++){
            if (board[i][j] == 0) {
                return [i, j]
            }
        }
    }
}
// 각 행(가로줄)에 반복되는 숫자가 없는지 검사
function rowsCheck(board){
    for (let i = 0; i < 9; i++){
        let cur = []
        for (let j = 0; j < 9; j++){
            if (cur.includes(board[i][j])){
                return false
            }
            else if (board[i][j] != 0){
                cur.push(board[i][j])
            }
        }
    }
    return true
}
// 각 열(세로줄)에 반복되는 숫자가 없는지 검사
function columnsCheck(board){
    for (let i = 0; i < 9; i++){
        let cur = []
        for (let j = 0; j < 9; j++){
            if (cur.includes(board[j][i])){
                return false
            }
            else if (board[j][i] != 0){
                cur.push(board[j][i])
            }
        }
    }
    return true
}
// 각 박스(3x3)에 반복되는 숫자가 없는지 검사
function boxesCheck(board){
    const boxCoordinates = [[0, 0], [0, 1], [0, 2],
                            [1, 0], [1, 1], [1, 2],
                            [2, 0], [2, 1], [2, 2]]
    for (let y = 0; y < 9; y += 3){
        for (let x = 0; x < 9; x += 3){
            // 순회는 각 상자를 검사해야 한다.
            let cur = []
            for (let i = 0; i < 9; i++){
                let coordinates = [...boxCoordinates[i]]
                coordinates[0] += y
                coordinates[1] += x
                if (cur.includes(board[coordinates[0]][coordinates[1]])){
                    return false
                }
                else if (board[coordinates[0]][coordinates[1]] != 0){
                    cur.push(board[coordinates[0]][coordinates[1]])
                }
            }
        }
    }
    return true
}
~~~

Data structure is a storage that is used to store and organize data. <br>
It is <span style='font-size:1em'>***a way of arranging data***</span> on a computer so that it can be accessed and updated efficiently.<br>

