í…ŒìŠ¤íŠ¸ì¼€ì´ìŠ¤ëŠ” ìœ íš¨í•œ ì…ë ¥ë§Œ ë“¤ì–´ì˜¤ëŠ” ìŠ¤ë„ì¿ ì´ë‚˜, ìœ íš¨í•œì§€ (ìŠ¤ë„ì¿  í•´ê²°ì´ ê°€ëŠ¥í•œ ì§€) íŒë³„í•˜ì—¬ ê°€ëŠ¥í•˜ë©´ í•´ê²°í•˜ë„ë¡ ì‘ì„±.

## Solution

~~~c
function solve(board) {
    if (solved(board)) {   
        return board  // ë‹¤ í•´ê²°ëë‹¤ë©´ í˜„ì¬ ë³´ë“œë¥¼ ë¦¬í„´
    } else {
        const possibilities = nextBoards(board)
        const validBoards = keepOnlyValid(possibilities)
        return searchForSolution(validBoards)
    }
}
// ëª©ë¡ì—ì„œ ì˜ëª»ëœ ë³´ë“œë¥¼ ëª¨ë‘ í•„í„°ë§
function keepOnlyValid(boards){
    let res = []
    for (let i = 0; i < boards.length; i++){
        if (validBoard(boards[i])){
            res.push(boards[i])
        }
    }
    return res
}
// ì£¼ì–´ì§„ ë³´ë“œê°€ ìœ íš¨í•œì§€ í™•ì¸
function validBoard(board){
    return rowsCheck(board) && columnsCheck(board) && boxesCheck(board)
}
// ë‹¤ í•´ê²°ëœ(ëª¨ë“  ì¹¸ì´ ì±„ì›Œì§„) ìŠ¤ë„ì¿ ì¸ì§€ ê²€ì‚¬
function solved(board){
    for (let i = 0; i < 9; i++){
        for (let j = 0; j < 9; j++){
            if (board[i][j] == 0){
                return false
            }
        }
    }
    return true
}
// í•´ê²°ì´ ì•ˆë˜ì—ˆë‹¤ë©´ ë°±íŠ¸ë˜í‚¹
function searchForSolution(boards) {
    if (boards.length < 1) {
        return false
    } else { 
        let first = boards.shift() 
        const tryPath = solve(first)
        if (tryPath != false) {
           return tryPath
        } else {
           return searchForSolution(boards)
        }
    }
}
// ì²« ë²ˆì§¸ ë¹ˆ ìë¦¬ë¥¼ ì°¾ê³  í•´ë‹¹ ìë¦¬ë¥¼ ìˆ«ì 1...9ë¡œ ì±„ìš°ëŠ” 9ê°œì˜ ë‹¤ë¥¸ ë³´ë“œë¥¼ ìƒì„±
function nextBoards(board){ 
    let res = []
    const firstEmpty = findEmptySquare(board)
    if (firstEmpty != undefined){
        const y = firstEmpty[0]
        const x = firstEmpty[1]
        for (let i = 1; i <= 9; i++){
            let newBoard = [...board]
            let row = [...newBoard[y]]
            row[x] = i
            newBoard[y] = row
            res.push(newBoard)
        }
    }
    return res
}
// ì²« ë²ˆì§¸ ë¹ˆ ìë¦¬ì— ëŒ€í•œ i j ì¢Œí‘œ ê°€ì ¸ì˜¤ê¸°)
function findEmptySquare(board){
    for (let i = 0; i < 9; i++){
        for (let j = 0; j < 9; j++){
            if (board[i][j] == 0) {
                return [i, j]
            }
        }
    }
}
// ê° í–‰(ê°€ë¡œì¤„)ì— ë°˜ë³µë˜ëŠ” ìˆ«ìê°€ ì—†ëŠ”ì§€ ê²€ì‚¬
function rowsCheck(board){
    for (let i = 0; i < 9; i++){
        let cur = []
        for (let j = 0; j < 9; j++){
            if (cur.includes(board[i][j])){
                return false
            }
            else if (board[i][j] != 0){
                cur.push(board[i][j])
            }
        }
    }
    return true
}
// ê° ì—´(ì„¸ë¡œì¤„)ì— ë°˜ë³µë˜ëŠ” ìˆ«ìê°€ ì—†ëŠ”ì§€ ê²€ì‚¬
function columnsCheck(board){
    for (let i = 0; i < 9; i++){
        let cur = []
        for (let j = 0; j < 9; j++){
            if (cur.includes(board[j][i])){
                return false
            }
            else if (board[j][i] != 0){
                cur.push(board[j][i])
            }
        }
    }
    return true
}
// ê° ë°•ìŠ¤(3x3)ì— ë°˜ë³µë˜ëŠ” ìˆ«ìê°€ ì—†ëŠ”ì§€ ê²€ì‚¬
function boxesCheck(board){
    const boxCoordinates = [[0, 0], [0, 1], [0, 2],
                            [1, 0], [1, 1], [1, 2],
                            [2, 0], [2, 1], [2, 2]]
    for (let y = 0; y < 9; y += 3){
        for (let x = 0; x < 9; x += 3){
            // ìˆœíšŒëŠ” ê° ìƒìë¥¼ ê²€ì‚¬í•´ì•¼ í•œë‹¤.
            let cur = []
            for (let i = 0; i < 9; i++){
                let coordinates = [...boxCoordinates[i]]
                coordinates[0] += y
                coordinates[1] += x
                if (cur.includes(board[coordinates[0]][coordinates[1]])){
                    return false
                }
                else if (board[coordinates[0]][coordinates[1]] != 0){
                    cur.push(board[coordinates[0]][coordinates[1]])
                }
            }
        }
    }
    return true
}
